mapper.insert:
insert into user_card_list (user_id, card_pk, count
)
values (#{userId,jdbcType=INTEGER}, #{cardPk,jdbcType=INTEGER}, #{count,jdbcType=INTEGER}
)

mapper.select:
select user_id, card_pk, count
from user_card_list
where user_id = #{userId} and card_pk = #{cardPk}

mapper.update:
update user_card_list
set count = #{count}
where user_id = #{userId} and card_pk = #{cardPk}

mapper.select:
select package_info.package_name, package_card.card_name, package_card.rare from package_info inner join package_card on package_info.package_id = package_card.package_id where package_name like concat("%", #{packageName} ,"%") and card_name like concat("%", #{cardName} ,"%")
and rare = #{rare}
order by package_card.package_id, package_card.rare, package_card.card_name

select package_info.package_name, package_card.card_name, package_card.rare, user_data.user_name, user_card_list.count from package_info inner join package_card on package_info.package_id = package_card.package_id inner join user_card_list on package_card.card_pk = user_card_list.card_pk inner join user_data on user_data.user_id = user_card_list.user_id where user_data.is_admin = 0 and package_name like concat("%", #{packageName} ,"%") and card_name like concat("%", #{cardName} ,"%")
and rare = #{rare}
and user_name like concat("%", #{userName} ,"%") order by package_card.package_id, package_card.rare, package_card.card_name, user_data.user_name

select count(1)
from user_card_list
where card_pk = #{cardPk}
